use serde::{Deserialize, Serialize};
use wasmi_core::UntypedValue;

use crate::mtable::{MemoryReadSize, MemoryStoreSize, VarType};

#[derive(Clone, Debug, Deserialize, Serialize)]
pub enum RunInstructionTracePre {
    BrIfEqz {
        condition: u64,
    },
    BrIfNez {
        condition: u64,
    },
    BrTable {
        index: u64,
    },
    Return {
        drop: u32,
        keep_values: Vec<u64>,
    },
    Call {
        args: Vec<u64>,
    },
    CallIndirect {
        idx: u32,
    },
    SetLocal {
        depth: usize,
        value: UntypedValue,
    },
    SetGlobal {
        idx: u32,
        value: UntypedValue,
    },
    Load {
        offset: u32,
        raw_address: u32,
        effective_address: Option<usize>, // use option in case of memory out of bound
        vtype: VarType,
        load_size: MemoryReadSize,
    },
    Store {
        offset: u32,
        raw_address: u32,
        effective_address: Option<usize>,
        value: u64,
        vtype: VarType,
        store_size: MemoryStoreSize,
        pre_block_value1: Option<u64>,
        pre_block_value2: Option<u64>,
    },
    GrowMemory {
        grow_size: u32,
        pages: u32,
    },

    I32BinOp {
        left: i32,
        right: i32,
    },
    I32BinShiftOp {
        left: u64,
        right: u64,
    },

    I64BinOp {
        left: i64,
        right: i64,
    },

    I32Single(i32),
    I32Comp {
        left: i32,
        right: i32,
    },
    I64Single(i64),
    I64Comp {
        left: i64,
        right: i64,
    },

    I32WrapI64 {
        value: i64,
    },
    I64ExtendI32 {
        value: u64,
        sign: bool,
    },
    I32SignExtendI8 {
        value: u64,
    },
    I32SignExtendI16 {
        value: u64,
    },
    I64SignExtendI8 {
        value: u64,
    },
    I64SignExtendI16 {
        value: u64,
    },
    I64SignExtendI32 {
        value: u64,
    },

    I32TruncF32 {
        value: u64,
        sign: bool,
    },

    I32TruncF64 {
        value: u64,
        sign: bool,
    },

    I64TruncF32 {
        value: u64,
        sign: bool,
    },

    I64TruncF64 {
        value: u64,
        sign: bool,
    },

    F32ConvertI32 {
        value: u64,
        sign: bool,
    },
    F32ConvertI64 {
        value: u64,
        sign: bool,
    },
    F64ConvertI32 {
        value: u64,
        sign: bool,
    },
    F64ConvertI64 {
        value: u64,
        sign: bool,
    },
    I32ReinterpretF32 {
        value: u64,
    },
    I64ReinterpretF64 {
        value: u64,
    },
    F32ReinterpretI32 {
        value: u64,
    },
    F64ReinterpretI64 {
        value: u64,
    },
    F32DemoteF64 {
        value: u64,
    },
    F64PromoteF32 {
        value: u64,
    },
    UnaryOp {
        operand: u64,
        vtype: VarType,
    },
    Drop,
    Select {
        val1: u64,
        val2: u64,
        cond: u64,
    },

    F32Comp {
        left: u64,
        right: u64,
    },

    F64Comp {
        left: u64,
        right: u64,
    },

    F32BinOp {
        left: u64,
        right: u64,
    },

    F64BinOp {
        left: u64,
        right: u64,
    },
    MemoryFill {
        offset: u64,
        value: u64,
        size: u64,
        val_vec: Vec<u64>,
    },
    MemoryCopy {
        num_bytes_to_copy: u64,
        src: u64,
        destination: u64,
        val_vec: Vec<u64>,
    },
}
